import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import { useCart } from '../context/CartContext';

const Home = () => {
  const [trendingProducts, setTrendingProducts] = useState([]);
  const [bestsellers, setBestsellers] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const { addToCart } = useCart();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [trendingRes, bestsellerRes, categoriesRes] = await Promise.all([
        api.get('/products/?trending=true&page_size=8'),
        api.get('/products/?bestseller=true&page_size=8'),
        api.get('/categories/'),
      ]);

      setTrendingProducts(trendingRes.data.results || trendingRes.data);
      setBestsellers(bestsellerRes.data.results || bestsellerRes.data);
      setCategories(categoriesRes.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  const handleAddToCart = async (productId, e) => {
    e.preventDefault();
    e.stopPropagation();
    try {
      await addToCart(productId, 1);
      alert('Product added to cart!');
    } catch (error) {
      alert('Please login to add items to cart');
    }
  };

  const ProductCard = ({ product }) => (
    <Link to={`/products/${product.slug}`} className="group">
      <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow">
        <div className="relative">
          <img
            src={product.image || 'https://via.placeholder.com/300'}
            alt={product.name}
            className="w-full h-64 object-cover"
          />
          {product.is_trending && (
            <span className="absolute top-2 left-2 bg-red-500 text-white px-2 py-1 text-xs font-bold rounded">
              TRENDING ðŸ”¥
            </span>
          )}
          {product.is_bestseller && (
            <span className="absolute top-2 left-2 bg-yellow-500 text-white px-2 py-1 text-xs font-bold rounded">
              BESTSELLER
            </span>
          )}
          {product.discount_percentage > 0 && (
            <span className="absolute top-2 right-2 bg-green-500 text-white px-2 py-1 text-xs font-bold rounded">
              {product.discount_percentage}% OFF
            </span>
          )}
        </div>
        <div className="p-4">
          <h3 className="font-semibold text-gray-800 mb-2 line-clamp-2">{product.name}</h3>
          <div className="flex items-center mb-2">
            <span className="text-yellow-400">â˜…</span>
            <span className="text-sm text-gray-600 ml-1">
              {product.rating} ({product.review_count} reviews)
            </span>
          </div>
          <div className="flex items-center justify-between">
            <div>
              {product.discount_price ? (
                <div>
                  <span className="text-lg font-bold text-purple-600">â‚¹{product.discount_price}</span>
                  <span className="text-sm text-gray-500 line-through ml-2">â‚¹{product.price}</span>
                </div>
              ) : (
                <span className="text-lg font-bold text-purple-600">â‚¹{product.price}</span>
              )}
            </div>
            <button
              onClick={(e) => handleAddToCart(product.id, e)}
              className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
            >
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </Link>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-purple-600 to-pink-600 text-white py-20">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-5xl font-bold mb-4">Plum Goodness</h1>
          <p className="text-xl mb-8">Natural Beauty Products for Your Skin, Body & Hair</p>
          <Link
            to="/products"
            className="bg-white text-purple-600 px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors inline-block"
          >
            Shop Now
          </Link>
        </div>
      </section>

      {/* Categories Section */}
      <section className="py-12 bg-gray-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold text-center mb-8">Shop by Category</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {categories.map((category) => (
              <Link
                key={category.id}
                to={`/products?category=${category.slug}`}
                className="bg-white p-6 rounded-lg shadow-md text-center hover:shadow-lg transition-shadow"
              >
                <h3 className="font-semibold text-gray-800">{category.name}</h3>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Trending Products */}
      <section className="py-12">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-6">Trending Products ðŸ”¥</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {trendingProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </div>
      </section>

      {/* Bestsellers */}
      <section className="py-12 bg-gray-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-6">Bestsellers</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {bestsellers.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-12 bg-purple-600 text-white">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 text-center">
            <div>
              <div className="text-4xl mb-4">ðŸšš</div>
              <h3 className="text-xl font-semibold mb-2">Free Delivery</h3>
              <p>On orders above â‚¹999</p>
            </div>
            <div>
              <div className="text-4xl mb-4">ðŸ”„</div>
              <h3 className="text-xl font-semibold mb-2">Easy Returns</h3>
              <p>30-day return policy</p>
            </div>
            <div>
              <div className="text-4xl mb-4">âœ¨</div>
              <h3 className="text-xl font-semibold mb-2">100% Natural</h3>
              <p>Certified organic products</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
