import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import { useCart } from '../context/CartContext';

const Home = () => {
  const [trendingProducts, setTrendingProducts] = useState([]);
  const [bestsellers, setBestsellers] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeFilter, setActiveFilter] = useState('bestsellers');
  const [filteredProducts, setFilteredProducts] = useState([]);
  const { addToCart } = useCart();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [trendingRes, bestsellerRes, categoriesRes] = await Promise.all([
        api.get('/products/?trending=true&page_size=8'),
        api.get('/products/?bestseller=true&page_size=8'),
        api.get('/categories/'),
      ]);

      setTrendingProducts(Array.isArray(trendingRes.data.results) ? trendingRes.data.results : (Array.isArray(trendingRes.data) ? trendingRes.data : []));
      setBestsellers(Array.isArray(bestsellerRes.data.results) ? bestsellerRes.data.results : (Array.isArray(bestsellerRes.data) ? bestsellerRes.data : []));
      setCategories(Array.isArray(categoriesRes.data.results) ? categoriesRes.data.results : (Array.isArray(categoriesRes.data) ? categoriesRes.data : []));
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  const handleAddToCart = async (productId, e) => {
    e.preventDefault();
    e.stopPropagation();
    try {
      await addToCart(productId, 1);
      alert('Product added to cart!');
    } catch (error) {
      alert('Please login to add items to cart');
    }
  };

  const ProductCard = ({ product }) => (
    <Link to={`/products/${product.slug}`} className="group">
      <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow">
        <div className="relative">
          <img
            src={product.image || 'https://via.placeholder.com/300'}
            alt={product.name}
            className="w-full h-64 object-cover"
          />
          {product.is_trending && (
            <span className="absolute top-2 left-2 bg-red-500 text-white px-2 py-1 text-xs font-bold rounded">
              TRENDING üî•
            </span>
          )}
          {product.is_bestseller && (
            <span className="absolute top-2 left-2 bg-yellow-500 text-white px-2 py-1 text-xs font-bold rounded">
              BESTSELLER
            </span>
          )}
          {product.discount_percentage > 0 && (
            <span className="absolute top-2 right-2 bg-green-500 text-white px-2 py-1 text-xs font-bold rounded">
              {product.discount_percentage}% OFF
            </span>
          )}
        </div>
        <div className="p-4">
          <h3 className="font-semibold text-gray-800 mb-2 line-clamp-2">{product.name}</h3>
          <div className="flex items-center mb-2">
            <span className="text-yellow-400">‚òÖ</span>
            <span className="text-sm text-gray-600 ml-1">
              {product.rating} ({product.review_count} reviews)
            </span>
          </div>
          <div className="flex items-center justify-between">
            <div>
              {product.discount_price ? (
                <div>
                  <span className="text-lg font-bold text-purple-600">‚Çπ{product.discount_price}</span>
                  <span className="text-sm text-gray-500 line-through ml-2">‚Çπ{product.price}</span>
                </div>
              ) : (
                <span className="text-lg font-bold text-purple-600">‚Çπ{product.price}</span>
              )}
            </div>
            <button
              onClick={(e) => handleAddToCart(product.id, e)}
              className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
            >
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </Link>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  // Category icons data
  const categoryIcons = [
    { name: 'new drops', slug: 'new', icon: 'üÜï', color: 'bg-purple-100' },
    { name: 'skincare', slug: 'skin', icon: 'üß¥', color: 'bg-blue-100' },
    { name: 'bodycare', slug: 'body', icon: 'üß¥', color: 'bg-yellow-100' },
    { name: 'fragrance', slug: 'fragrances', icon: 'üíé', color: 'bg-amber-100' },
    { name: 'haircare', slug: 'hair', icon: 'üß¥', color: 'bg-blue-100' },
    { name: 'combos', slug: 'gifting', icon: 'üéÅ', color: 'bg-purple-100' },
  ];

  return (
    <div className="min-h-screen bg-purple-50">
      {/* Category Icons Row */}
      <section className="py-8 bg-white">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center gap-6 overflow-x-auto">
            {categoryIcons.map((cat) => (
              <Link
                key={cat.slug}
                to={`/products?category=${cat.slug}`}
                className="flex flex-col items-center space-y-2 min-w-[100px] group"
              >
                <div className={`w-20 h-20 rounded-full ${cat.color} border-4 border-yellow-400 flex items-center justify-center text-3xl group-hover:scale-110 transition-transform shadow-lg`}>
                  {cat.icon}
                </div>
                <span className="text-sm font-medium text-gray-700 capitalize">{cat.name}</span>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Main Promotion Section - Season Finale Sale */}
      <section className="relative py-16 bg-gradient-to-br from-purple-600 via-purple-700 to-purple-800 overflow-hidden">
        {/* Sparkle/Confetti Effect */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute animate-pulse"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animationDelay: `${Math.random() * 2}s`,
                animationDuration: `${2 + Math.random() * 2}s`,
              }}
            >
              <span className="text-yellow-300 text-2xl">‚ú®</span>
            </div>
          ))}
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <div className="grid md:grid-cols-3 gap-8 items-center">
            {/* Left: Disco Ball with Sale Text */}
            <div className="text-center md:text-left">
              <div className="inline-block bg-gradient-to-br from-purple-400 to-purple-600 rounded-full p-8 shadow-2xl transform rotate-12 hover:rotate-0 transition-transform duration-500">
                <h2 className="text-2xl md:text-3xl font-bold text-white mb-2">Plum</h2>
                <h3 className="text-xl md:text-2xl font-bold text-yellow-300 mb-1">SEASON FINALE</h3>
                <h3 className="text-xl md:text-2xl font-bold text-white">SALE</h3>
              </div>
            </div>

            {/* Center: Product Display */}
            <div className="flex flex-wrap justify-center gap-4">
              {trendingProducts.slice(0, 5).map((product, idx) => (
                <div
                  key={product.id}
                  className="bg-white rounded-lg p-3 shadow-xl transform hover:scale-105 transition-transform"
                  style={{ animationDelay: `${idx * 0.1}s` }}
                >
                  <img
                    src={product.image || 'https://via.placeholder.com/100'}
                    alt={product.name}
                    className="w-20 h-20 md:w-24 md:h-24 object-contain"
                  />
                </div>
              ))}
            </div>

            {/* Right: Buy Get 2 Free Banner */}
            <div className="text-center md:text-right">
              <div className="inline-block bg-purple-800 border-4 border-yellow-400 rounded-lg p-6 shadow-2xl">
                <h3 className="text-2xl md:text-3xl font-bold text-white mb-2">
                  BUY GET 2 FREE
                </h3>
                <p className="text-yellow-300 font-semibold text-sm md:text-base">
                  + extra gifts on all orders
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* WhatsApp Icon - Fixed Position */}
        <a
          href="https://wa.me/1234567890"
          target="_blank"
          rel="noopener noreferrer"
          className="fixed bottom-6 right-6 bg-green-500 hover:bg-green-600 text-white rounded-full p-4 shadow-2xl z-50 transition-transform hover:scale-110"
        >
          <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
            <path d="M17.472 14.382c-.297-.149-1.758-.867-2.03-.967-.273-.099-.471-.148-.67.15-.197.297-.767.966-.94 1.164-.173.199-.347.223-.644.075-.297-.15-1.255-.463-2.39-1.475-.883-.788-1.48-1.761-1.653-2.059-.173-.297-.018-.458.13-.606.134-.133.298-.347.446-.52.149-.174.198-.298.298-.497.099-.198.05-.371-.025-.52-.075-.149-.669-1.612-.916-2.207-.242-.579-.487-.5-.669-.51-.173-.008-.371-.01-.57-.01-.198 0-.52.074-.792.372-.272.297-1.04 1.016-1.04 2.479 0 1.462 1.065 2.875 1.213 3.074.149.198 2.096 3.2 5.077 4.487.709.306 1.262.489 1.694.625.712.227 1.36.195 1.871.118.571-.085 1.758-.719 2.006-1.413.248-.694.248-1.289.173-1.413-.074-.124-.272-.198-.57-.347m-5.421 7.403h-.004a9.87 9.87 0 01-5.031-1.378l-.361-.214-3.741.982.998-3.648-.235-.374a9.86 9.86 0 01-1.51-5.26c.001-5.45 4.436-9.884 9.888-9.884 2.64 0 5.122 1.03 6.988 2.898a9.825 9.825 0 012.893 6.994c-.003 5.45-4.437 9.884-9.885 9.884m8.413-18.297A11.815 11.815 0 0012.05 0C5.495 0 .16 5.335.157 11.892c0 2.096.547 4.142 1.588 5.945L.057 24l6.305-1.654a11.882 11.882 0 005.683 1.448h.005c6.554 0 11.89-5.335 11.893-11.893a11.821 11.821 0 00-3.48-8.413Z"/>
          </svg>
        </a>
      </section>

      {/* Trending Products */}
      <section className="py-12 bg-white">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-6 text-gray-800">Trending Products üî•</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {trendingProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </div>
      </section>

      {/* Bestsellers */}
      <section className="py-12 bg-gray-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-6 text-gray-800">Bestsellers</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {bestsellers.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-12 bg-purple-700 text-white">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 text-center">
            <div>
              <div className="text-4xl mb-4">üöö</div>
              <h3 className="text-xl font-semibold mb-2">Free Delivery</h3>
              <p>On orders above ‚Çπ999</p>
            </div>
            <div>
              <div className="text-4xl mb-4">üîÑ</div>
              <h3 className="text-xl font-semibold mb-2">Easy Returns</h3>
              <p>30-day return policy</p>
            </div>
            <div>
              <div className="text-4xl mb-4">‚ú®</div>
              <h3 className="text-xl font-semibold mb-2">100% Natural</h3>
              <p>Certified organic products</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
