import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import { useCart } from '../context/CartContext';

const ProductDetail = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);
  const { addToCart } = useCart();

  useEffect(() => {
    fetchProduct();
  }, [slug]);

  const fetchProduct = async () => {
    try {
      setLoading(true);
      const response = await api.get(`/products/?slug=${slug}`);
      const products = response.data.results || response.data;
      if (products.length > 0) {
        setProduct(products[0]);
        if (products[0].images && products[0].images.length > 0) {
          setSelectedImage(0);
        }
      }
      setLoading(false);
    } catch (error) {
      console.error('Error fetching product:', error);
      setLoading(false);
    }
  };

  const handleAddToCart = async () => {
    try {
      await addToCart(product.id, quantity);
      alert('Product added to cart!');
    } catch (error) {
      alert('Please login to add items to cart');
    }
  };

  const images = product?.images && product.images.length > 0 
    ? product.images 
    : product?.image 
      ? [product.image] 
      : ['https://via.placeholder.com/500'];

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="text-center py-20">
        <p className="text-gray-600 text-lg">Product not found</p>
        <button
          onClick={() => navigate('/products')}
          className="mt-4 bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700"
        >
          Back to Products
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        <div className="bg-white rounded-lg shadow-lg p-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {/* Image Gallery */}
            <div>
              <div className="mb-4">
                <img
                  src={images[selectedImage]}
                  alt={product.name}
                  className="w-full h-96 object-cover rounded-lg"
                />
              </div>
              {images.length > 1 && (
                <div className="flex space-x-2 overflow-x-auto">
                  {images.map((img, index) => (
                    <button
                      key={index}
                      onClick={() => setSelectedImage(index)}
                      className={`flex-shrink-0 w-20 h-20 rounded-lg overflow-hidden border-2 ${
                        selectedImage === index ? 'border-purple-600' : 'border-gray-300'
                      }`}
                    >
                      <img src={img} alt={`${product.name} ${index + 1}`} className="w-full h-full object-cover" />
                    </button>
                  ))}
                </div>
              )}
            </div>

            {/* Product Info */}
            <div>
              <div className="mb-4">
                {product.is_trending && (
                  <span className="inline-block bg-red-500 text-white px-3 py-1 text-sm font-bold rounded mr-2">
                    TRENDING ðŸ”¥
                  </span>
                )}
                {product.is_bestseller && (
                  <span className="inline-block bg-yellow-500 text-white px-3 py-1 text-sm font-bold rounded mr-2">
                    BESTSELLER
                  </span>
                )}
                {product.is_new && (
                  <span className="inline-block bg-green-500 text-white px-3 py-1 text-sm font-bold rounded">
                    NEW
                  </span>
                )}
              </div>

              <h1 className="text-3xl font-bold mb-4">{product.name}</h1>

              <div className="flex items-center mb-4">
                <span className="text-yellow-400 text-2xl">â˜…</span>
                <span className="text-lg text-gray-700 ml-2">
                  {product.rating} ({product.review_count} reviews)
                </span>
              </div>

              <div className="mb-6">
                {product.discount_price ? (
                  <div>
                    <span className="text-4xl font-bold text-purple-600">â‚¹{product.discount_price}</span>
                    <span className="text-xl text-gray-500 line-through ml-3">â‚¹{product.price}</span>
                    <span className="ml-3 text-green-600 font-semibold">
                      {product.discount_percentage}% OFF
                    </span>
                  </div>
                ) : (
                  <span className="text-4xl font-bold text-purple-600">â‚¹{product.price}</span>
                )}
              </div>

              <div className="mb-6">
                <p className="text-gray-700 leading-relaxed">{product.description}</p>
              </div>

              <div className="mb-6 space-y-2">
                <div>
                  <span className="font-semibold">Category: </span>
                  <span className="text-gray-600">{product.category?.name}</span>
                </div>
                <div>
                  <span className="font-semibold">Skin Type: </span>
                  <span className="text-gray-600">{product.skin_type}</span>
                </div>
                <div>
                  <span className="font-semibold">Stock: </span>
                  <span className={product.stock > 0 ? 'text-green-600' : 'text-red-600'}>
                    {product.stock > 0 ? 'In Stock' : 'Out of Stock'}
                  </span>
                </div>
              </div>

              {/* Quantity Selector */}
              <div className="mb-6">
                <label className="block font-semibold mb-2">Quantity:</label>
                <div className="flex items-center space-x-4">
                  <button
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                    className="bg-gray-200 px-4 py-2 rounded-lg hover:bg-gray-300"
                  >
                    -
                  </button>
                  <span className="text-xl font-semibold">{quantity}</span>
                  <button
                    onClick={() => setQuantity(quantity + 1)}
                    className="bg-gray-200 px-4 py-2 rounded-lg hover:bg-gray-300"
                  >
                    +
                  </button>
                </div>
              </div>

              {/* Add to Cart Button */}
              <div className="space-y-4">
                <button
                  onClick={handleAddToCart}
                  disabled={product.stock === 0}
                  className={`w-full py-3 rounded-lg font-semibold text-white transition-colors ${
                    product.stock > 0
                      ? 'bg-purple-600 hover:bg-purple-700'
                      : 'bg-gray-400 cursor-not-allowed'
                  }`}
                >
                  {product.stock > 0 ? 'Add to Cart' : 'Out of Stock'}
                </button>
                <button
                  onClick={() => navigate('/checkout')}
                  disabled={product.stock === 0}
                  className={`w-full py-3 rounded-lg font-semibold border-2 transition-colors ${
                    product.stock > 0
                      ? 'border-purple-600 text-purple-600 hover:bg-purple-50'
                      : 'border-gray-400 text-gray-400 cursor-not-allowed'
                  }`}
                >
                  Buy Now
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
